settingstext.relatedproducts.xsl:
&lt;?xml version=&quot;1.0&quot; encoding=&quot;utf-8&quot;?&gt;

&lt;xsl:stylesheet version=&quot;1.0&quot; xmlns:xsl=&quot;http://www.w3.org/1999/XSL/Transform&quot;  xmlns:msxsl=&quot;urn:schemas-microsoft-com:xslt&quot; exclude-result-prefixes=&quot;msxsl&quot;&gt;
    &lt;xsl:output method=&quot;xml&quot; indent=&quot;yes&quot; omit-xml-declaration=&quot;yes&quot; /&gt;
&lt;xsl:template match=&quot;/root&quot;&gt;

&lt;div class=&quot;RelatedWrapper&quot;&gt;
&lt;xsl:for-each select=&quot;related/item&quot;&gt;
	&lt;div class=&quot;RelatedOuter&quot;&gt;
		&lt;div class=&quot;RelatedInner&quot;&gt;
			&lt;a&gt;
  &lt;xsl:attribute name=&quot;href&quot;&gt;&lt;xsl:value-of select=&quot;./link&quot; disable-output-escaping=&quot;yes&quot; /&gt;&lt;/xsl:attribute&gt;  
	  		&lt;img&gt;
				&lt;xsl:attribute name=&quot;alt&quot;&gt;				
				&lt;xsl:value-of select=&quot;./P/NB_Store_ProductsInfo/ProductName&quot; /&gt;				
				&lt;/xsl:attribute&gt;
				&lt;xsl:attribute name=&quot;title&quot;&gt;				
				&lt;xsl:value-of select=&quot;./P/NB_Store_ProductsInfo/Summary&quot; /&gt;				
				&lt;/xsl:attribute&gt;
				&lt;xsl:attribute name=&quot;src&quot;&gt;				
				&lt;xsl:value-of select=&quot;/root/ModulePath&quot; /&gt;makethumbnail.ashx?Image=&lt;xsl:value-of select=&quot;./P/I/NB_Store_ProductImageInfo/ImageID&quot; /&gt;&amp;amp;w=120&amp;amp;tabid=&lt;xsl:value-of select=&quot;/root/TabID&quot; /&gt;				
				&lt;/xsl:attribute&gt;
			&lt;/img&gt;&lt;/a&gt;
			&lt;div class=&quot;Name&quot;&gt;
&lt;xsl:value-of select=&quot;./P/NB_Store_ProductsInfo/ProductName&quot; /&gt;&lt;/div&gt;
			&lt;div class=&quot;Price&quot;&gt;
&lt;xsl:value-of select=&quot;./frompricecurrency&quot; /&gt;&lt;/div&gt;
		&lt;/div&gt;
	&lt;/div&gt;
&lt;/xsl:for-each&gt;
&lt;/div&gt;
&lt;/xsl:template&gt;
&lt;/xsl:stylesheet&gt;

